/**
 * @param type - "evm" or "solana", extend this union as needed (e.g., "sui")
 * @param id - Chain ID, optional for EVM
 */
type Chain = EvmChain | SolanaChain | AptosChain | ChromiaChain | FuelChain | SuiChain | ZilliqaChain | CosmosChain | StarknetChain | RadixChain | ZetrixChain;
type SuiChain = {
    type: "sui";
};
type EvmChain = {
    type: "evm";
    id: number;
};
type SolanaChain = {
    type: "solana";
};
type AptosChain = {
    type: "aptos";
};
type ChromiaChain = {
    type: "chromia";
};
type FuelChain = {
    type: "fuel";
};
type ZilliqaChain = {
    type: "zilliqa";
    id: number;
    evmId: number;
};
type CosmosChain = {
    type: "cosmos";
};
type StarknetChain = {
    type: "starknet";
};
type RadixChain = {
    type: "radix";
    id: number;
};
type ZetrixChain = {
    type: "zetrix";
};

export type { AptosChain, Chain, ChromiaChain, CosmosChain, EvmChain, FuelChain, RadixChain, SolanaChain, StarknetChain, SuiChain, ZetrixChain, ZilliqaChain };
